import App, { AppInitialProps } from 'next/app';
import { Provider } from 'react-redux';
import { NextPageContext } from 'next';
import { Store } from 'redux';
import store from '../store/store';
import withRedux from "next-redux-wrapper";

interface AppContext extends NextPageContext {
    store: Store;
}

class MoviesApp extends App<AppContext> {

    static async getInitialProps({Component, context}: any) {
        // const pageProps = Component.getInitialProps ?
        // await Component.getInitialProps(context) :
        // {}

        // return {pageProps: pageProps} as AppInitialProps;
        return new Promise<AppInitialProps>(res => res({} as AppInitialProps))
    }

    render() {
        const {Component, pageProps, store} = this.props;
        return (
            <Provider store={store}>
                <Component {...pageProps} />
            </Provider>
        )
    }
}

const makeStore = () => store as any;

export default withRedux(makeStore)(MoviesApp);
